CREATE OR REPLACE PROCEDURE deletar_atributo_visao ( nm_tabela_p text, nm_atributo_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	tabela_visao
	where	nm_tabela	= nm_tabela_p;

BEGIN

OPEN C01;
LOOP
FETCH C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	delete	FROM tabela_visao_atributo
	where	nr_sequencia	= nr_sequencia_w
	and	nm_atributo	= nm_atributo_p;

	end;
END LOOP;
CLOSE C01;

COMMIT;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
