CREATE OR REPLACE PROCEDURE deletar_dietas_prescricao (nr_prescricao_p bigint, nr_seq_min_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	prescr_dieta
where	nr_prescricao = nr_prescricao_p
and		nr_sequencia  > nr_seq_min_p;


BEGIN
open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
		CALL deletar_dieta(nr_prescricao_p,nr_sequencia_w,'D',nm_usuario_p);
end loop;
close C01;

end;
$body$
