CREATE OR REPLACE PROCEDURE deletar_dieta ( nr_prescricao_p bigint, nr_seq_item_p bigint, ie_tipo_item_p text, nm_usuario_p text) AS $body$
DECLARE


nr_atendimento_w	bigint;
nr_sequencia_w		bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	prescr_material a,
	material b
where	a.nr_prescricao = nr_prescricao_p
and	a.cd_material	= b.cd_material
and	coalesce(a.ie_se_necessario,'N') 	<> 'S'
and	coalesce(a.ie_acm,'N') 		<> 'S'
and	a.ie_agrupador	in (8,12,16,17)
and	not exists (	select	1
			from	prescr_mat_hor b
			where	a.nr_prescricao 	= b.nr_prescricao
			and	b.nr_seq_material 	= a.nr_sequencia);


BEGIN

if (ie_tipo_item_p = 'D') then
	delete from prescr_dieta
	where	nr_prescricao = nr_prescricao_p
	and	nr_sequencia	= nr_seq_item_p;
elsif (ie_tipo_item_p = 'M') then
	delete from prescr_material
	where	nr_prescricao = nr_prescricao_p
	and	nr_sequencia	= nr_seq_item_p;
end if;

select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

delete	FROM prescr_mat_hor x
where	x.nr_prescricao	= nr_prescricao_p
and	x.ie_agrupador	in (8,12,16,17)
and	coalesce(x.dt_lib_horario::text, '') = ''
and	exists (SELECT	1
		from	prescr_medica b,
			rep_jejum a
		where	b.nr_atendimento	= nr_atendimento_w
		and	b.nr_prescricao		= a.nr_prescricao
		and	coalesce(a.ie_suspenso,'N')	<> 'S'
		and	coalesce(b.dt_suspensao::text, '') = ''
		and	x.dt_horario between coalesce(a.dt_inicio, b.dt_inicio_prescr) and coalesce(a.dt_fim, b.dt_validade_prescr));

delete	FROM prescr_dieta_hor x
where	x.nr_prescricao	= nr_prescricao_p
and	coalesce(x.dt_lib_horario::text, '') = ''
and	exists (SELECT	1
		from	prescr_medica b,
			rep_jejum a
		where	b.nr_atendimento	= nr_atendimento_w
		and	b.nr_prescricao		= a.nr_prescricao
		and	coalesce(a.ie_suspenso,'N')	<> 'S'
		and	coalesce(b.dt_suspensao::text, '') = ''
		and	x.dt_horario between coalesce(a.dt_inicio, b.dt_inicio_prescr) and coalesce(a.dt_fim, b.dt_validade_prescr));

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	delete	FROM prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_sequencia_w;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
