CREATE OR REPLACE PROCEDURE deletar_horarios_dieta_jejum ( nr_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_item_p bigint, ie_tipo_item_p text, nm_usuario_p text) AS $body$
DECLARE


dt_inicio_w	timestamp;
dt_fim_w	timestamp;
ie_agrupador_w	smallint;


BEGIN
if (1 = 2) then
	select	max(dt_inicio),
		max(dt_fim -1/1440)
	into STRICT	dt_inicio_w,
		dt_fim_w
	from	rep_jejum a,
		prescr_medica b
	where	a.nr_prescricao		= b.nr_prescricao
	and	coalesce(b.dt_suspensao::text, '') = ''
	and	coalesce(a.ie_suspenso,'N') <> 'S'
	and	b.nr_atendimento	= nr_atendimento_p;

	if (ie_tipo_item_p	= 'M') then

		select	max(ie_agrupador)
		into STRICT	ie_agrupador_w
		from	prescr_material
		where	nr_prescricao	= nr_prescricao_p
		and	nr_sequencia	= nr_seq_item_p;

		if (ie_agrupador_w <> 8) then

			delete from prescr_mat_hor a
			where	a.nr_prescricao	= nr_prescricao_p
			and	a.nr_seq_material	= nr_seq_item_p
			and	coalesce(a.dt_lib_horario::text, '') = ''
			and	a.dt_horario between dt_inicio_w and dt_fim_w
			and	coalesce(a.ie_horario_especial,'N') <> 'S';

		end if;

	elsif (ie_tipo_item_p	= 'D') then
		delete from prescr_dieta_hor a
		where	a.nr_prescricao	= nr_prescricao_p
		and	a.nr_seq_dieta	= nr_seq_item_p
		and	coalesce(a.dt_lib_horario::text, '') = ''
		and	a.dt_horario between dt_inicio_w and dt_fim_w;
	end if;

	commit;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
