CREATE OR REPLACE PROCEDURE deletar_inf_adicional (nr_atendimento_p bigint) AS $body$
DECLARE


cd_medico_w     atendimento_paciente_inf.cd_medico%TYPE;


BEGIN
  IF (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') THEN

      SELECT max(cd_medico)
      INTO STRICT cd_medico_w
      FROM atendimento_paciente_inf
      WHERE nr_atendimento = nr_atendimento_p;

      DELETE FROM atendimento_paciente_inf
      WHERE nr_atendimento = nr_atendimento_p;

      IF (coalesce(pkg_i18n.get_user_locale, 'pt_BR') = 'de_AT')
            and (cd_medico_w IS NOT NULL AND cd_medico_w::text <> '') THEN
          DELETE FROM atend_consent_carta_med
          WHERE nr_atendimento = nr_atendimento_p
          AND cd_medico = cd_medico_w;
      END IF;
  END IF;
  COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
