CREATE OR REPLACE PROCEDURE deletar_itens_contrato_selec ( nr_sequencia_p bigint, ie_opcao_p bigint, ie_utilizacao_p text, nm_usuario_p text) AS $body$
DECLARE


/* ie_opcao_p
0 - Tudo
1 - Somente da sequencia
*/
BEGIN

if (ie_opcao_p = 0) then
	delete	FROM w_itens_contrato_selec
	where	coalesce(ie_utilizacao,'SC') = ie_utilizacao_p;
else	if (ie_opcao_p = 1) then
	delete	FROM w_itens_contrato_selec
	where	nr_sequencia = nr_sequencia_p
	and	coalesce(ie_utilizacao,'SC') = ie_utilizacao_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
