CREATE OR REPLACE PROCEDURE deletar_itens_trev ( nr_seq_rep_he_p bigint) AS $body$
DECLARE


nr_seq_rep_he_elem_w	bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	prescr_rep_he_elem
where	nr_seq_rep_he = nr_seq_rep_he_p;


BEGIN

open c01;
loop
fetch c01 into nr_seq_rep_he_elem_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
