	v_query := 'CALL deletar_jejum_atx ( ' || quote_nullable(nr_sequencia_p) || ',' || quote_nullable(dt_inicio_p) || ',' || quote_nullable(dt_fim_p) || ',' || quote_nullable(nr_prescricao_p) || ',' || quote_nullable(nm_usuario_p) || ' )';
