CREATE OR REPLACE PROCEDURE deletar_jejum_atx ( nr_sequencia_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE
nr_prescricao_w		bigint;
nr_sequencia_w		bigint;
nr_seq_dieta_w		bigint;
nr_atendimento_w	bigint;
ie_hor_susp_w		varchar(1);

c01 CURSOR FOR
SELECT	b.nr_prescricao,
	c.nr_sequencia,
	b.nr_sequencia
from	prescr_medica a,
	prescr_material b,
	prescr_mat_hor c
where	a.nr_prescricao	= b.nr_prescricao
and	a.nr_prescricao	= c.nr_prescricao
and	b.nr_prescricao	= c.nr_prescricao
and	b.nr_sequencia	= c.nr_seq_material
and	a.nr_atendimento = nr_atendimento_w
and	c.nr_seq_jejum_susp = nr_sequencia_p
and	c.dt_horario	between dt_inicio_p and dt_fim_p;

c02 CURSOR FOR
SELECT	b.nr_prescricao,
	c.nr_sequencia,
	b.nr_sequencia
from	prescr_medica a,
	prescr_dieta b,
	prescr_dieta_hor c
where	a.nr_prescricao	= b.nr_prescricao
and	a.nr_prescricao	= c.nr_prescricao
and	b.nr_prescricao	= c.nr_prescricao
and	b.nr_sequencia	= c.nr_seq_dieta
and	a.nr_atendimento = nr_atendimento_w
and	c.nr_seq_jejum_susp = nr_sequencia_p
and	c.dt_horario	between dt_inicio_p and dt_fim_p;


BEGIN

select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then

	open C01;
	loop
	fetch C01 into
		nr_prescricao_w,
		nr_sequencia_w,
		nr_seq_dieta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	prescr_mat_hor
		set	nr_seq_jejum_susp	 = NULL,
			dt_suspensao		 = NULL,
			nm_usuario_susp		 = NULL,
			ie_suspenso_adep	= 'N'
		where	nr_sequencia		= nr_sequencia_w;

		select	CASE WHEN count(nr_sequencia)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_hor_susp_w
		from	prescr_mat_hor
		where	nr_prescricao		= nr_prescricao_w
		and	nr_seq_material		= nr_seq_dieta_w
		and	(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '');

		update	prescr_material
		set	ie_horario_susp		= ie_hor_susp_w
		where	nr_prescricao		= nr_prescricao_w
		and	nr_sequencia		= nr_seq_dieta_w;

		end;
	end loop;
	close C01;

	open C02;
	loop
	fetch C02 into
		nr_prescricao_w,
		nr_sequencia_w,
		nr_seq_dieta_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		update	prescr_dieta_hor
		set	nr_seq_jejum_susp	 = NULL,
			dt_suspensao		 = NULL,
			nm_usuario_susp		 = NULL
		where	nr_sequencia		= nr_sequencia_w;

		select	CASE WHEN count(nr_sequencia)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_hor_susp_w
		from	prescr_dieta_hor
		where	nr_prescricao		= nr_prescricao_w
		and	nr_seq_dieta		= nr_seq_dieta_w
		and	(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '');

		update	prescr_dieta
		set	ie_horario_susp		= ie_hor_susp_w
		where	nr_prescricao		= nr_prescricao_w
		and	nr_sequencia		= nr_seq_dieta_w;

		end;
	end loop;
	close C02;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
