CREATE OR REPLACE PROCEDURE deletar_lista_espera_pf (cd_pessoa_fisica_p text, cd_agenda_p bigint) AS $body$
BEGIN
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	 
	delete	FROM agenda_lista_espera a 
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p 
	and	a.cd_agenda		<> cd_agenda_p 
	and	a.ie_status_espera	not in ('C','E') 
	and	not exists (SELECT 1 from agenda_consulta x where x.nr_seq_lista_espera = a.nr_sequencia);
 
end if;	
	 
commit;
 
end;
$body$
