CREATE OR REPLACE PROCEDURE deletar_medicos_import ( ie_origem_p text) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	nr_sequencia
	from	cadastro_medico
	where	ie_origem_import = ie_origem_p;

c01_w				c01%rowtype;
qt_registros_atualizados_w	bigint := 0;


BEGIN
if	not(coalesce(ie_origem_p::text, '') = '') then
	open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		delete
		from	cadastro_medico
		where	nr_sequencia = c01_w.nr_sequencia;
		qt_registros_atualizados_w := qt_registros_atualizados_w + 1;

		if (qt_registros_atualizados_w = 100) then
			commit;
			qt_registros_atualizados_w := 0;
		end if;

	end;
	end loop;
	close C01;
end if;

commit;

end;
