CREATE OR REPLACE PROCEDURE deletar_pacote ( nr_seq_pacote_p bigint, dt_vigencia_p timestamp, nm_usuario_p text) AS $body$
BEGIN
if	((nr_seq_pacote_p IS NOT NULL AND nr_seq_pacote_p::text <> '') and (dt_vigencia_p IS NOT NULL AND dt_vigencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '')) then
	begin

	delete	from pacote_material
	where	nr_seq_pacote	= nr_seq_pacote_p
	and	nr_seq_pac_acomod in (	SELECT	nr_sequencia
					from	pacote_tipo_acomodacao
					where	dt_vigencia	= dt_vigencia_p
					and	nr_seq_pacote	= nr_seq_pacote_p );

	delete	from pacote_procedimento
	where	nr_seq_pacote	= nr_seq_pacote_p
	and	nr_seq_pac_acomod in (	SELECT	nr_sequencia
					from	pacote_tipo_acomodacao
					where	dt_vigencia	= dt_vigencia_p
					and	nr_seq_pacote	= nr_seq_pacote_p );

	delete	from pacote_tipo_acomodacao
	where	nr_seq_pacote	= nr_seq_pacote_p
	and	dt_vigencia	= dt_vigencia_p;

	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
