CREATE OR REPLACE PROCEDURE deletar_prescricao_filha ( nr_prescricao_p bigint) AS $body$
BEGIN

delete 	from prescr_medica a
where 	nr_prescricao_anterior = nr_prescricao_p
and	coalesce(dt_liberacao::text, '') = ''
and	coalesce(dt_liberacao_medico::text, '') = ''
and	not exists (SELECT 1 from prescr_material x where a.nr_prescricao = x.nr_prescricao and x.nr_prescricao_original = nr_prescricao_p);

commit;

end;
