CREATE OR REPLACE PROCEDURE deletar_registro_isbt ( nr_prescricao_p bigint, nr_seq_procedimento_p bigint, nr_bolsa_p text, nm_usuario_p text, nr_seq_horario_p bigint default 0) AS $body$
DECLARE

 
nr_sequencia_w	bigint;
				

BEGIN 
 
if (coalesce(nr_seq_horario_p,0) > 0) then 
	delete	 
	from	prescr_proc_bolsa 
	where	nr_prescricao		= nr_prescricao_p 
	and		nr_seq_horario		= nr_seq_horario_p;
 
else 
	delete	 
	from	prescr_proc_bolsa 
	where	nr_prescricao		= nr_prescricao_p 
	and		nr_seq_procedimento	= nr_seq_procedimento_p;
end if;
 
if (coalesce(nr_prescricao_p,0) > 0) then 
	/* obter sequencia */
 
	select	nextval('prescr_solucao_evento_seq') 
	into STRICT	nr_sequencia_w 
	;
 
	/* gerar evento */
 
	insert into prescr_solucao_evento( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		nr_prescricao, 
		nr_seq_solucao, 
		nr_seq_material, 
		nr_seq_procedimento, 
		nr_seq_nut, 
		nr_seq_nut_neo, 
		ie_forma_infusao, 
		ie_tipo_dosagem, 
		qt_dosagem, 
		qt_vol_infundido, 
		qt_vol_desprezado, 
		qt_volume_parcial, 
		cd_pessoa_fisica, 
		ie_alteracao, 
		dt_alteracao, 
		ie_evento_valido, 
		nr_seq_motivo, 
		ds_observacao, 
		ie_tipo_solucao, 
		qt_volume_fase, 
		nr_etapa_evento) 
	values ( 
		nr_sequencia_w, 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_prescricao_p, 
		null, 
		null, 
		nr_seq_procedimento_p, 
		null, 
		null, 
		null, 
		null, 
		null, 
		null, 
		null, 
		null, 
		obter_dados_usuario_opcao(nm_usuario_p, 'C'), 
		29, 
		clock_timestamp(), 
		'S', 
		null, 
		null, 
		3, 
		null, 
		null);
end if;
 
commit;
 
end;
