CREATE OR REPLACE PROCEDURE deletar_reserva_fila_espera ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_possui_reserva_w			varchar(1);
ie_gerar_nova_reserva_w		varchar(1);
									

BEGIN 
ie_gerar_nova_reserva_w := Obter_param_Usuario(1002, 122, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_gerar_nova_reserva_w);
 
if (coalesce(nr_sequencia_p,0) > 0) then 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_possui_reserva_w 
	from	fila_espera_reserva 
	where	nr_seq_gv	= nr_sequencia_p;
		 
	if (ie_possui_reserva_w = 'S')	and (ie_gerar_nova_reserva_w = 'S')	then 
		delete	FROM fila_espera_reserva 
		where	nr_seq_gv = nr_sequencia_p;
		commit;
		CALL Reservar_leito(nr_sequencia_p, nm_usuario_p);
	end if;
end if;
	 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
