CREATE OR REPLACE PROCEDURE deletar_rothman_sem_retorno ( qt_horas_p bigint) AS $body$
DECLARE



C01 CURSOR FOR
	SELECT	nr_sequencia
	from	escala_rothman
	where	coalesce(qt_score::text, '') = ''
	and		DT_AVALIACAO	< clock_timestamp() - (1/qt_horas_p);
nr_sequencia_w	bigint;

BEGIN
open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	delete from	escala_rothman
	where	nr_sequencia = nr_sequencia_w;

	end;
end loop;
close C01;

commit;

end;
