CREATE OR REPLACE PROCEDURE deletar_simpro_preco ( ie_data_p bigint, dt_data_p timestamp, cd_simpro_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_estab_simpro_w	estabelecimento.cd_estabelecimento%type;


BEGIN

cd_estab_simpro_w := wheb_usuario_pck.get_cd_estabelecimento;

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (ie_data_p IS NOT NULL AND ie_data_p::text <> '') and (dt_data_p IS NOT NULL AND dt_data_p::text <> '')  then
	begin
	if (ie_data_p = 0) then
		begin
			delete
			from 	simpro_preco
			where 	trunc(dt_vigencia,'dd') = trunc(dt_data_p,'dd')
			and  	((coalesce(cd_simpro_p,0) = 0) or (cd_simpro = cd_simpro_p))
			and     coalesce(cd_estabelecimento, coalesce(cd_estab_simpro_w, 0)) = coalesce(cd_estab_simpro_w, 0);
		end;
	elsif (ie_data_p = 1)then
		begin
			delete
			from 	simpro_preco
			where 	trunc(dt_atualizacao,'dd') = trunc(dt_data_p,'dd')
			and  	((coalesce(cd_simpro_p,0) = 0) or (cd_simpro = cd_simpro_p))
			and     coalesce(cd_estabelecimento, coalesce(cd_estab_simpro_w, 0)) = coalesce(cd_estab_simpro_w, 0);
		end;
	end if;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
