CREATE OR REPLACE PROCEDURE deletar_w_titulo_abatimento ( nr_titulo_p bigint, nr_bordero_p bigint, ie_pagar_receber_p text) AS $body$
BEGIN
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') or (nr_bordero_p IS NOT NULL AND nr_bordero_p::text <> '') then
	begin
	delete
	from 	w_titulo_abatimento
	where 	coalesce(nr_titulo,0) = coalesce(nr_titulo_p,coalesce(nr_titulo,0))
	and	nr_bordero = coalesce(nr_bordero_p,nr_bordero)
	and	ie_pagar_receber = coalesce(ie_pagar_receber_p,ie_pagar_receber);
	commit;
	end;
end if;
end;
$body$
