CREATE OR REPLACE PROCEDURE delete_cpoe_just_int_proc (nr_sequencia_p bigint, cd_material_p bigint, ie_commit_p text default 'S') AS $body$
DECLARE


C01 CURSOR FOR
SELECT 	distinct
		nr_seq_proc_princ
from 	cpoe_justif_interacao
where 	nr_seq_proc_cpoe = nr_sequencia_p
and 	nr_seq_proc_princ = nr_seq_proc_cpoe;

C02 CURSOR FOR
SELECT 	distinct
		nr_seq_proc_princ
from 	cpoe_justif_interacao
where	nr_seq_proc_cpoe = nr_sequencia_p
and 	nr_seq_proc_princ <> nr_seq_proc_cpoe;
BEGIN

for c01_w in C01
loop
	delete	FROM cpoe_justif_interacao
	where	nr_seq_proc_princ = nr_sequencia_p
	and		cd_material = cd_material_p;
end loop;

for c02_w in C02
loop
	delete	FROM cpoe_justif_interacao
	where	nr_seq_proc_princ = c02_w.nr_seq_proc_princ
	and		cd_material_interacao = cd_material_p;
end loop;

delete	FROM cpoe_justif_interacao
where	nr_seq_proc_cpoe = nr_sequencia_p
and		cd_material = cd_material_p;

if (ie_commit_p = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
