CREATE OR REPLACE PROCEDURE delete_emergency_data_nfd_dpe ( cd_pessoa_fisica_p text, nr_report_p integer, nm_usuario_p text ) AS $body$
DECLARE


nr_sequencia_w		emergency_data_nfdm.nr_sequencia%type;
cd_pessoa_fisica_w	emergency_data_nfdm.cd_pessoa_fisica%type;


BEGIN

begin
    select	max(cd_pessoa_fisica)
    into STRICT	cd_pessoa_fisica_w
    from	emergency_data_nfdm where cd_pessoa_fisica = cd_pessoa_fisica_p;
    exception
        when no_data_found then
        cd_pessoa_fisica_w := null;
end;

if (not coalesce(cd_pessoa_fisica_w::text, '') = '') then
    if (nr_report_p = 0) then
        update	emergency_data_nfdm set
            ds_nfd                   = NULL,
            dt_atualizacao	        = clock_timestamp(),
            nm_usuario		        = nm_usuario_p
        where   cd_pessoa_fisica    = cd_pessoa_fisica_p;
    else
        update	emergency_data_nfdm set
            ds_dpe		             = NULL,
            dt_atualizacao		    = clock_timestamp(),
            nm_usuario		        = nm_usuario_p
        where   cd_pessoa_fisica    = cd_pessoa_fisica_p;
    end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
