SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE delete_hotfix ( nm_branch_p text, nm_usuario_p text, ie_cancelar_marcacao text default 'N') AS $body$
DECLARE


nm_usuario_marcacao_w log_deleted_hotfix.nm_usuario%type;
qt_registros_w bigint;


BEGIN

if (ie_cancelar_marcacao = 'S') then

    delete
    from log_deleted_hotfix a
    where a.nm_branch = nm_branch_p
    and a.nm_usuario = nm_usuario_p
    and a.ie_exclusao_confirmada = 'N';

else
    begin

        select max(nm_usuario_nrec)
        into STRICT nm_usuario_marcacao_w
        from log_deleted_hotfix a
        where a.nm_branch = nm_branch_p
        and dt_atualizacao = (
            SELECT max(a.dt_atualizacao)
            from log_deleted_hotfix b
            where b.nm_branch = nm_branch_p
        );

    exception
        when no_data_found then nm_usuario_marcacao_w := null;
    end;

    if (nm_usuario_marcacao_w != nm_usuario_p and (nm_usuario_marcacao_w IS NOT NULL AND nm_usuario_marcacao_w::text <> '')) then
    
        delete from service_order_sp_hotfix a
        where a.nr_service_pack_hotfix in (
            SELECT b.nr_sequencia from service_pack_hotfix b
            where b.cd_version||'.'||ds_hotfix = nm_branch_p
        );

        delete from service_pack_hotfix
        where cd_version||'.'||ds_hotfix = nm_branch_p;

        update log_deleted_hotfix
        set
            nm_usuario = nm_usuario_p,
            dt_atualizacao = clock_timestamp(),
            ie_exclusao_confirmada = 'S'
        where nm_branch = nm_branch_p;

    elsif (coalesce(nm_usuario_marcacao_w::text, '') = '') then
    
        insert into log_deleted_hotfix(
            nr_sequencia,
            dt_atualizacao,
            dt_atualizacao_nrec,
            nm_usuario,
            nm_usuario_nrec,
            nm_branch,
            ie_exclusao_confirmada
        ) values (
            nextval('log_deleted_hotfix_seq'),
            clock_timestamp(),
            clock_timestamp(),
            nm_usuario_p,
            nm_usuario_p,
            nm_branch_p,
            'N'
        );

    end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
