CREATE OR REPLACE PROCEDURE delete_lab_soro_armazena_rack ( nr_sequencia_p bigint ) AS $body$
DECLARE

qt_info_conclusao bigint;

BEGIN

    SELECT COUNT(1)
    INTO STRICT   qt_info_conclusao
    FROM   lab_soro_processo_info
    WHERE  nr_seq_armazena_rack = nr_sequencia_p
       AND ie_acao <> 'AR';

    IF (qt_info_conclusao <> 0) THEN
        CALL wheb_mensagem_pck.exibir_mensagem_abort(1024775, '');
    END IF;

    DELETE FROM lab_soro_processo_info
    WHERE       nr_seq_armazena_rack = nr_sequencia_p;

    DELETE FROM lab_soro_armazena_rack
    WHERE       nr_sequencia = nr_sequencia_p;

END;
