CREATE OR REPLACE PROCEDURE delete_material_from_codebar ( cd_material_p bigint, nr_seq_motivo_p bigint, cd_kit_material_p bigint, nr_sequencia_p bigint, nm_usuario_p text, nr_seq_kit_p bigint) AS $body$
BEGIN
delete
  from COMPONENTE_KIT_OPCAO a
 where a.nr_sequencia      = nr_sequencia_p
   and a.cd_kit_material   = cd_kit_material_p
   and a.nm_usuario        = nm_usuario_p;

delete
  from COMPONENTE_KIT c 
 where c.nr_sequencia    = nr_sequencia_p
   and c.cd_kit_material = cd_kit_material_p
   and c.nm_usuario      = nm_usuario_p;

update	kit_estoque_comp
   set	ie_gerado_barras		= 'E',
	    nr_seq_motivo_exclusao	= nr_seq_motivo_p,
	    dt_exclusao		        = clock_timestamp(),
	    nm_usuario_exclusao	    = nm_usuario_p
 where	cd_material             = cd_material_p
 and    nr_seq_kit_estoque      = nr_seq_kit_p;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
