CREATE OR REPLACE PROCEDURE delete_patients_found (qt_dias_p bigint default 7) AS $body$
DECLARE


qt_dias_w	bigint;

BEGIN
if (coalesce(qt_dias_p,0) <= 0) then
	qt_dias_w := 7;
else
	qt_dias_w := qt_dias_p;
end if;

delete	FROM RECENT_PATIENTS_FOUND
where 	trunc(dt_atualizacao) < trunc(clock_timestamp()) - qt_dias_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
