CREATE OR REPLACE PROCEDURE delete_prescr_diag ( NR_SEQ_PRESC_P PE_PRESCRICAO.NR_SEQUENCIA%TYPE ) AS $body$
BEGIN
    DELETE
    from PE_PRESCR_DIAG a
    WHERE
        a.nr_seq_prescr = NR_SEQ_PRESC_P AND
        NOT EXISTS (
            SELECT 1
            FROM pe_result_item_diag c,
                pe_prescr_item_result d
            WHERE c.nr_seq_result = d.nr_seq_result
                AND d.NR_SEQ_PRESCR = a.NR_SEQ_PRESCR
                AND c.nr_seq_diag = a.nr_seq_DIAG
            );
END;
