CREATE OR REPLACE PROCEDURE delete_prescr_interv ( NR_SEQ_PRESCR_P PE_PRESCRICAO.NR_SEQUENCIA%TYPE, NR_SEQ_PROC_P PE_PROCEDIMENTO.NR_SEQUENCIA%TYPE ) AS $body$
DECLARE


nr_qtd_interv_w bigint;

c_intervention CURSOR FOR
SELECT pci.nr_sequencia, pci.NR_SEQ_PAT_CP_INTERV_PLAN
from patient_cp_intervention pci, cp_intervention ci, pe_prescr_diag ppd
where pci.NR_SEQ_CP_INTERVENTION = ci.nr_sequencia
and ppd.nr_sequencia = pci.NR_SEQ_PRESCR_DIAG
and ci.NR_SEQ_PE_PROCEDIMENTO = nr_seq_proc_p
and ppd.NR_SEQ_PRESCR = nr_seq_prescr_p;

BEGIN

for r_c_intervention in c_intervention loop
delete from PATIENT_CP_INTERVENTION
where nr_sequencia = r_c_intervention.nr_sequencia;

commit;

select count(*)
into STRICT nr_qtd_interv_w
from PATIENT_CP_INTERVENTION 
where NR_SEQ_PAT_CP_INTERV_PLAN = r_c_intervention.NR_SEQ_PAT_CP_INTERV_PLAN;

if (nr_qtd_interv_w = 0) then
  delete from PATIENT_CP_INTERV_PLAN
  where nr_sequencia = r_c_intervention.NR_SEQ_PAT_CP_INTERV_PLAN;

commit;

end if;

end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
