CREATE OR REPLACE PROCEDURE delete_record_signed_in_batch ( nr_sequencia_p tasy_assinatura_lote.nr_sequencia%type, nr_project_code_p tasy_projeto_assinatura.nr_sequencia%type ) AS $body$
DECLARE


nm_tabela_w 				TASY_ASSINATURA_LOTE.NM_TABELA%type;
ie_tipo_assinatura_w		TASY_ASSINATURA_LOTE.IE_TIPO_ASSINATURA%type;
ie_deletar_w 				varchar(1) := 'S';
										

BEGIN

	select max(coalesce(a.nm_tabela,b.nm_tabela)), max(a.ie_tipo_assinatura)
	into STRICT   nm_tabela_w, ie_tipo_assinatura_w
	from   TASY_ASSINATURA_LOTE a, TASY_PROJETO_ASSINATURA b
	where  a.nr_seq_registro = nr_sequencia_p
	and    a.nr_proj_assinatura = b.nr_sequencia
	and    a.nr_proj_assinatura = nr_project_code_p;

   select	verifica_se_assinado(nm_tabela_w, nr_project_code_p, nr_sequencia_p, ie_tipo_assinatura_w)
   into STRICT		ie_deletar_w 
;

   	if (ie_deletar_w <> 'N') then

		delete 
		from tasy_assinatura_lote a
		where a.nr_seq_registro = nr_sequencia_p
		and a.nr_proj_assinatura = nr_project_code_p;

	commit;
	
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
