CREATE OR REPLACE PROCEDURE delete_trans_financ_holding ( nr_sequencia_p transacao_financeira.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE


c01 CURSOR FOR
	SELECT	t.nr_sequencia
  	from  transacao_financeira t
  	where  t.nr_seq_trans_fin_ref  = nr_sequencia_p;

c01_w		c01%rowtype;

BEGIN

open c01;
loop
fetch c01 into	
	c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		delete from trans_financ_campo
		where nr_seq_trans_financ = c01_w.nr_sequencia;
		
		delete from trans_financ_usuario
		where nr_seq_transacao = c01_w.nr_sequencia;
		
		delete from trans_financ_conta_ctb
		where nr_seq_trans_financ = c01_w.nr_sequencia;
		
		delete from trans_financ_contab
		where nr_seq_trans_financ = c01_w.nr_sequencia;
		
		delete from transacao_financeira
		where nr_sequencia = c01_w.nr_sequencia;
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
