CREATE OR REPLACE PROCEDURE delete_w_cr_topazio ( nm_usuario_p text, ie_acao_p text, ds_conteudo_p text) AS $body$
BEGIN

if (ie_acao_p = 'D') then
delete from W_CR_TOPAZIO;
else
insert into W_CR_TOPAZIO(	NR_SEQUENCIA,
			DT_ATUALIZACAO,
			NM_USUARIO,
