SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE del_titulo_pagar_imposto_par ( nr_variavel_p bigint, ie_opcao_p text) AS $body$
BEGIN

if (nr_variavel_p IS NOT NULL AND nr_variavel_p::text <> '') then

	if (ie_opcao_p = 'B') then

		delete	from w_titulo_pagar_imposto
		where	nr_bordero = nr_variavel_p;

	elsif (ie_opcao_p = 'E') then

		delete from w_titulo_pagar_imposto
		where	nr_seq_escrit = nr_variavel_p;

	elsif (ie_opcao_p = 'T') then

		delete from w_titulo_pagar_imposto
		where	coalesce(nr_seq_escrit::text, '') = ''
		and	coalesce(nr_bordero::text, '') = ''
		and	nr_titulo = nr_variavel_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
