SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desbobrar_itens_nao_aprovados (nr_sequencia_autor_cir_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_autor_w	bigint;
nr_seq_mat_autor_w	bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	material_autor_cirurgia a,
		autorizacao_cirurgia b
	where	a.nr_seq_autorizacao = b.nr_sequencia
	and	a.nr_seq_autorizacao = nr_sequencia_autor_cir_p
	and	coalesce(a.ie_aprovacao,'N') = 'N'
	and	coalesce(a.qt_material,0) = 0
	and	coalesce(a.qt_solicitada,0) > 0
	and	b.ie_estagio_autor = '3';

BEGIN


open C01;
loop
fetch C01 into
	nr_seq_mat_autor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (nr_seq_mat_autor_w > 0) then

		select	nextval('autorizacao_cirurgia_seq')
		into STRICT	nr_sequencia_autor_w
		;

		insert into Autorizacao_cirurgia(
			cd_comprador,
			cd_estabelecimento,
			cd_pessoa_fisica,
			cd_procedimento,
			cd_requisitante,
			cd_senha,
			ds_justificativa,
			ds_motivo_negativa,
			ds_observacao,
			dt_atualizacao,
			dt_autorizacao,
			dt_cancelamento,
			dt_fim_cotacao,
			dt_liberacao,
			dt_liberacao_autor,
			dt_pedido,
			dt_previsao,
			ie_estagio_autor,
			ie_origem_proced,
			nm_autorizante,
			nm_usuario,
			nm_usuario_autorizacao,
			nm_usuario_cancel,
			nm_usuario_lib,
			nm_usuario_pedido,
			nr_atendimento,
			nr_doc_convenio,
			nr_prescricao,
			nr_seq_agenda,
			nr_seq_agenda_consulta,
			nr_seq_autor_conv,
			nr_seq_classif,
			nr_seq_proc_interno,
			nr_sequencia)
		SELECT	cd_comprador,
			cd_estabelecimento,
			cd_pessoa_fisica,
			cd_procedimento,
			cd_requisitante,
			cd_senha,
			ds_justificativa,
			ds_motivo_negativa,
			wheb_mensagem_pck.get_Texto(311007, 'NR_SEQUENCIA_AUTOR_CIR_P='|| NR_SEQUENCIA_AUTOR_CIR_P), /*'Gerado apartir da autorização' || nr_sequencia_autor_cir_p || ' Pois ficaram itens pendentes de aprovação ', */
			clock_timestamp(),
			null,
			null,
			null,
			null,
			null,
			clock_timestamp(),
			null,
			'1',
			ie_origem_proced,
			null,
			nm_usuario_p,
			null,
			null,
			null,
			nm_usuario_pedido,
			nr_atendimento,
			nr_doc_convenio,
			nr_prescricao,
			nr_seq_agenda,
			nr_seq_agenda_consulta,
			nr_seq_autor_conv,
			nr_seq_classif,
			nr_seq_proc_interno,
			nr_sequencia_autor_w
		from	autorizacao_cirurgia
		where	nr_sequencia = nr_sequencia_autor_cir_p;


		update	material_autor_cirurgia
		set	nr_seq_autorizacao = nr_sequencia_autor_w,
			nm_usuario	   = nm_usuario_p,
			dt_atualizacao	   = clock_timestamp()
		where	nr_seq_autorizacao = nr_sequencia_autor_cir_p
		and	nr_sequencia	   = nr_seq_mat_autor_w;

		end if;
	end;
end loop;
close C01;



commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
