CREATE OR REPLACE PROCEDURE descartar_diagnostico_pend ( nr_sequencia_p text, nm_usuario_p text, ds_justificativa_p text) AS $body$
DECLARE


nr_sequencia_w bigint;WITH RECURSIVE cte AS (


c01 CURSOR FOR
	SELECT regexp_substr(nr_sequencia_p,'[^,]+', 1, level)
	 
	(regexp_substr(nr_sequencia_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_sequencia_p, '[^,]+', 1, level))::text <> '')  UNION ALL


c01 CURSOR FOR
	SELECT regexp_substr(nr_sequencia_p,'[^,]+', 1, level) JOIN cte c ON ()

) SELECT * FROM cte;
;
	

BEGIN


open c01;
loop
fetch c01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
begin
	update 	ATEND_DIAGNOSTICO_PENDENTE
	set		ie_situacao = 'I',
			dt_inativacao = clock_timestamp(),
			nm_usuario_inativacao = nm_usuario_p,
			ds_justificativa = ds_justificativa_p
	where 	nr_sequencia = nr_sequencia_w;
end;
end loop;
close c01;

commit;

end;
$body$
