CREATE OR REPLACE PROCEDURE desconciliar_sem_contrapartida ( nr_seq_movto_trans_p bigint, nr_seq_ext_lanc_p bigint, nm_usuario_p text) AS $body$
BEGIN
	if (nr_seq_movto_trans_p <> 0) then
	   begin
	   update	movto_trans_financ
	   set 	ie_conciliacao	 = 'N'
	   where 	nr_sequencia 	 = nr_seq_movto_trans_p;
	   end;

	elsif (nr_seq_ext_lanc_p <> 0) then
	   begin
	   update	banco_extrato_lanc
	   set 	ie_conciliacao	= 'N'
	   where 	nr_sequencia         	= nr_seq_ext_lanc_p;
                   end;

	end if;

commit;

end;
$body$
