CREATE OR REPLACE PROCEDURE desconfirmar_agenda_tasy ( dt_agenda_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_seq_ageconvite_w	bigint;

C01 CURSOR FOR
	SELECT	b.nr_sequencia
	from	agenda_tasy b,
		agenda_tasy_convite a
	where	a.nr_seq_agenda	= b.nr_sequencia
	and	b.ie_status		= 'M'
	and	b.dt_agenda		= dt_agenda_p
	and	nm_usuario_convidado	= nm_usuario_p
	and	a.ie_confirmado	= 'S';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_ageconvite_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	agenda_tasy_convite
	set	ie_confirmado	= 'N',
		dt_confirmacao	 = NULL,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_seq_agenda	= nr_seq_ageconvite_w
	and	nm_usuario_convidado	= nm_usuario_p;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
