SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desdobrar_historico_audit ( nr_sequencia_p bigint, vl_hist_audit_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_historico_novo_w	double precision;
vl_historico_w		double precision;
nr_sequencia_w		bigint;
nr_seq_conpaci_ret_w	bigint;
nr_seq_hist_audit_w	bigint;


BEGIN

select	vl_historico,
	nr_seq_conpaci_ret,
	nr_seq_hist_audit
into STRICT	vl_historico_w,
	nr_seq_conpaci_ret_w,
	nr_seq_hist_audit_w
from	conta_paciente_ret_hist
where	nr_sequencia	= nr_sequencia_p;

if (vl_hist_audit_p <= vl_historico_w) then
	vl_historico_novo_w	:= vl_historico_w - vl_hist_audit_p;
else
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(277621);
end if;

update	conta_paciente_ret_hist
set	vl_historico		= vl_hist_audit_p
where	nr_sequencia		= nr_sequencia_p;

select	nextval('conta_paciente_ret_hist_seq')
into STRICT	nr_sequencia_w
;

insert	into conta_paciente_ret_hist(
					nr_sequencia,
					dt_historico,
					vl_historico,
					dt_atualizacao,
					nm_usuario,
					nr_seq_conpaci_ret,
					nr_seq_hist_audit,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nm_usuario_resp)
				values (
					nr_sequencia_w,
					clock_timestamp(),
					vl_historico_novo_w,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_conpaci_ret_w,
					nr_seq_hist_audit_w,
					clock_timestamp(),
					nm_usuario_p,
					nm_usuario_p);




commit;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
