SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desdobrar_item_devolucao ( nr_devolucao_p bigint, nr_sequencia_p bigint, cd_local_estoque_p bigint, qt_material_estoque_p bigint, cd_tipo_baixa_p text, nm_usuario_p text, nr_seq_item_p INOUT bigint) AS $body$
DECLARE


nr_sequencia_w			bigint;
qt_material_estoque_w		double precision;
qt_material_w			double precision;
qt_material_novo_w		double precision;


BEGIN

nr_seq_item_p		:= 0;
qt_material_w		:= 0;
qt_material_novo_w	:= 0;
qt_material_estoque_w	:= coalesce(qt_material_estoque_p,0);

select	a.qt_material
into STRICT	qt_material_w
from	devolucao_material_pac b,
	item_devolucao_material_pac a
where	a.nr_devolucao	= b.nr_devolucao
and	a.nr_sequencia	= nr_sequencia_p
and	a.nr_devolucao	= nr_devolucao_p;

qt_material_novo_w	:= qt_material_w - qt_material_estoque_w;

if (qt_material_novo_w > 0) then

	/* Gerar registro com quantidade que não foi lida (diferença) */

	select	coalesce(max(nr_sequencia),0) + 1
	into STRICT	nr_sequencia_w
	from	item_devolucao_material_pac
	where	nr_devolucao	= nr_devolucao_p;

	insert into item_devolucao_material_pac(
		nr_devolucao,
		nr_sequencia,
		cd_material,
		dt_atendimento,
		cd_local_estoque,
		cd_unidade_medida,
		cd_motivo_devolucao,
		dt_atualizacao,
		nm_usuario_devol,
		qt_material,
		nr_prescricao,
		nr_sequencia_prescricao,
		cd_pessoa_fisica_receb,
		dt_recebimento,
		nm_usuario_receb,
		qt_material_estoque,
		dt_entrada_unidade,
		nr_seq_atendimento,
		cd_material_dev,
		cd_cgc_fornec,
		nm_usuario,
		dt_emissao_loc_estoque,
		cd_setor_atendimento,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_geracao,
		dt_baixa_conta,
		nm_usuario_baixa_conta,
		ie_tipo_baixa_estoque)
	SELECT	nr_devolucao_p,
		nr_sequencia_w,
		a.cd_material,
		a.dt_atendimento,
		cd_local_estoque_p,
		a.cd_unidade_medida,
		a.cd_motivo_devolucao,
		clock_timestamp(),
		a.nm_usuario_devol,
		qt_material_novo_w,
		nr_prescricao,
		nr_sequencia_prescricao,
		cd_pessoa_fisica_receb,
		dt_recebimento,
		nm_usuario_receb,
		null,
		a.dt_entrada_unidade,
		a.nr_seq_atendimento,
		a.cd_material_dev,
		a.cd_cgc_fornec,
		nm_usuario_p,
		a.dt_emissao_loc_estoque,
		a.cd_setor_atendimento,
		clock_timestamp(),
		nm_usuario_p,
		'S',
		dt_baixa_conta,
		nm_usuario_baixa_conta,
		'0'
	from	item_devolucao_material_pac a
	where	a.nr_devolucao	= nr_devolucao_p
	and	a.nr_sequencia	= nr_sequencia_p;

	/* Atualiza quantidade a devolver com a quantidade lida */

	update	item_devolucao_material_pac
	set	qt_material	= qt_material_estoque_w
	where	nr_devolucao	= nr_devolucao_p
	and	nr_sequencia	= nr_sequencia_p;
	commit;
	nr_seq_item_p	:= coalesce(nr_Sequencia_w,0);
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
