CREATE OR REPLACE PROCEDURE desdobrar_item_ordem_compra ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_divisor_w		double precision;
qt_loop_w		double precision;
qt_material_w		double precision;
qt_material_ww 		double precision;
nr_item_oci_w		integer;
consiste_contrato_w smallint;


BEGIN

select	trunc(qt_material),
	qt_material
into STRICT	qt_loop_w,
	qt_material_w
from	ordem_compra_item
where	nr_ordem_compra	= nr_ordem_compra_p
and	nr_item_oci	= nr_item_oci_p;

select count(*)
into STRICT consiste_contrato_w
from  contrato_regra_nf
where nr_ordem_compra = nr_ordem_compra_p
and nr_item_oci = nr_item_oci_p;

if (consiste_contrato_w >= 1) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(341766, 'NR_ITEM_OCI_P='||nr_item_oci_p);
end if;

qt_material_ww := round((dividir(qt_material_w, qt_loop_w))::numeric,4);

while(qt_loop_w > 0) loop
	begin

	select	coalesce(max(nr_item_oci),0) + 1
	into STRICT	nr_item_oci_w
	from	ordem_compra_item
	where	nr_ordem_compra = nr_ordem_compra_p;

	insert into ordem_compra_item(
		nr_ordem_compra,
		nr_item_oci,
		cd_material,
		cd_unidade_medida_compra,
		vl_unitario_material,
		qt_material,
		dt_atualizacao,
		nm_usuario,
		ie_situacao,
		cd_pessoa_solicitante,
		qt_material_entregue,
		pr_descontos,
		cd_local_estoque,
		ds_material_direto,
		ds_observacao,
		cd_motivo_alteracao,
		nr_cot_compra,
		nr_item_cot_compra,
		ds_marca,
		vl_item_liquido,
		nr_seq_aprovacao,
		dt_aprovacao,
		cd_centro_custo,
		cd_conta_contabil,
		nr_solic_compra,
		nr_item_solic_compra,
		qt_conv_unid_fornec,
		ie_geracao_solic,
		nr_seq_proj_rec,
		pr_desc_financ,
		nr_seq_lic_item,
		nr_seq_conta_financ,
		qt_original,
		dt_validade,
		nr_seq_marca,
		dt_reprovacao,
		nr_seq_ordem_serv,
		vl_ultima_compra,
		vl_dif_ultima_compra,
		pr_dif_ultima_compra,
		nr_seq_unidade_adic,
		nr_seq_criterio_rateio,
		nr_serie_material,
		nr_solic_compra_cancel,
		vl_desconto,
		nr_seq_lote_fornec,
		nr_seq_proj_gpi,
		nr_seq_etapa_gpi,
		nr_seq_conta_gpi,
		dt_aprovacao_orig,
		dt_reprovacao_orig,
		nr_contrato,
		dt_inicio_garantia,
		dt_fim_garantia,
		nr_id_integracao,
		nr_seq_reg_lic_item,
		nr_seq_conta_bco,
		nr_seq_prescr_item,
		nr_empenho,
		dt_empenho,
		nr_ordem_agrup,
		nr_atendimento,
		vl_total_item)
	SELECT	nr_ordem_compra_p,
		nr_item_oci_w,
		cd_material,
		cd_unidade_medida_compra,
		vl_unitario_material,
		qt_material_ww,
		clock_timestamp(),
		nm_usuario_p,
		ie_situacao,
		cd_pessoa_solicitante,
		null,
		pr_descontos,
		cd_local_estoque,
		ds_material_direto,
		ds_observacao,
		cd_motivo_alteracao,
		nr_cot_compra,
		nr_item_cot_compra,
		ds_marca,
		0,
		nr_seq_aprovacao,
		dt_aprovacao,
		cd_centro_custo,
		cd_conta_contabil,
		nr_solic_compra,
		nr_item_solic_compra,
		qt_conv_unid_fornec,
		ie_geracao_solic,
		nr_seq_proj_rec,
		pr_desc_financ,
		nr_seq_lic_item,
		nr_seq_conta_financ,
		qt_material_ww,
		dt_validade,
		nr_seq_marca,
		dt_reprovacao,
		nr_seq_ordem_serv,
		vl_ultima_compra,
		vl_dif_ultima_compra,
		pr_dif_ultima_compra,
		nr_seq_unidade_adic,
		nr_seq_criterio_rateio,
		nr_serie_material,
		nr_solic_compra_cancel,
		vl_desconto,
		nr_seq_lote_fornec,
		nr_seq_proj_gpi,
		nr_seq_etapa_gpi,
		nr_seq_conta_gpi,
		dt_aprovacao_orig,
		dt_reprovacao_orig,
		nr_contrato,
		dt_inicio_garantia,
		dt_fim_garantia,
		nr_id_integracao,
		nr_seq_reg_lic_item,
		nr_seq_conta_bco,
		nr_seq_prescr_item,
		nr_empenho,
		dt_empenho,
		nr_ordem_agrup,
		nr_atendimento,
		round((qt_material_ww * vl_unitario_material)::numeric,4)
	from	ordem_compra_item
	where	nr_ordem_compra = nr_ordem_compra_p
	and	nr_item_oci = nr_item_oci_p;

	insert into ordem_compra_item_entrega(
		nr_ordem_compra,
		nr_item_oci,
		dt_prevista_entrega,
		dt_real_entrega,
		qt_prevista_entrega,
		qt_real_entrega,
		dt_atualizacao,
		nm_usuario,
		ds_observacao,
		dt_cancelamento,
		nr_sequencia,
		ie_status_exportar,
		dt_entrega_original,
		dt_entrega_limite,
		dt_baixa)
	SELECT	nr_ordem_compra_p,
		nr_item_oci_w,
		dt_prevista_entrega,
		dt_real_entrega,
		qt_material_ww,
		qt_real_entrega,
		clock_timestamp(),
		nm_usuario_p,
		ds_observacao,
		dt_cancelamento,
		nextval('ordem_compra_item_entrega_seq'),
		ie_status_exportar,
		dt_entrega_original,
		dt_entrega_limite,
		dt_baixa
	from	ordem_compra_item_entrega
	where	nr_ordem_compra = nr_ordem_compra_p
	and	nr_item_oci	= nr_item_oci_p;

	qt_loop_w := qt_loop_w - 1;
	end;
end loop;

delete from ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
