CREATE OR REPLACE PROCEDURE desdobrar_ordens_compra ( nr_ordem_compra_p bigint) AS $body$
BEGIN

delete   FROM ordem_compra_item_entrega
where    nr_ordem_compra = nr_ordem_compra_p
and      qt_prevista_entrega = 0;

delete   FROM ordem_compra_item
where    nr_ordem_compra = nr_ordem_compra_p
and      qt_material = 0;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
