SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desdobrar_proc_compra ( nr_seq_aprovacao_p bigint, nr_seq_processo_p bigint, nm_usuario_p text, ds_seq_itens_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
ds_retorno_w			varchar(40);



BEGIN

select nextval('processo_compra_seq')
into STRICT nr_sequencia_w
;

insert into processo_compra(
	nr_sequencia,
	cd_processo_aprov,
	dt_atualizacao,
	nm_usuario)
SELECT	nr_sequencia_w,
	cd_processo_aprov,
	clock_timestamp(),
	nm_usuario_p
from	processo_compra
where	nr_sequencia 	= nr_seq_aprovacao_p;

insert into processo_aprov_compra(
	nr_sequencia,
	nr_seq_proc_aprov,
	dt_atualizacao,
	nm_usuario,
	cd_pessoa_fisica,
	cd_cargo,
	dt_liberacao,
	dt_definicao,
	ie_aprov_reprov,
	ie_motivo_reprovacao,
	nm_usuario_nrec,
	ds_observacao,
	cd_estabelecimento,
	ie_urgente,
	nr_documento,
	ie_tipo,
	dt_documento,
	nr_nivel_aprovacao,
	cd_processo_aprov,
	ie_responsavel,
	vl_minimo,
	vl_maximo,
	qt_minimo_aprovador,
	nm_usuario_regra,
	qt_itens_regra,
	qt_intervalo_regra)
SELECT	nr_sequencia_w,
	nr_seq_proc_aprov,
	clock_timestamp(),
	nm_usuario_p,
	cd_pessoa_fisica,
	cd_cargo,
	dt_liberacao,
	dt_definicao,
	ie_aprov_reprov,
	ie_motivo_reprovacao,
	nm_usuario_p,
	ds_observacao,
	cd_estabelecimento,
	ie_urgente,
	nr_documento,
	ie_tipo,
	dt_documento,
	nr_nivel_aprovacao,
	cd_processo_aprov,
	ie_responsavel,
	vl_minimo,
	vl_maximo,
	qt_minimo_aprovador,
	nm_usuario_regra,
	qt_itens_regra,
	qt_intervalo_regra
from	processo_aprov_compra
where	nr_sequencia		= nr_seq_aprovacao_p
and	nr_seq_proc_aprov		>= nr_seq_processo_p;

ds_retorno_w := obter_valor_dinamico(	' update ordem_compra_item 		' ||
			' set nr_seq_aprovacao	= 	' || nr_sequencia_w	||
			'    ,dt_desdobr_aprov  = sysdate ' ||
			' where nr_seq_aprovacao= 		' || nr_seq_aprovacao_p ||
			' and nr_item_oci in (		' || ds_seq_itens_p 	|| ')', ds_retorno_w);

ds_retorno_w := obter_valor_dinamico(	' update Solic_compra_item 		' ||
			' set nr_seq_aprovacao	= 	' || nr_sequencia_w	||
			'    ,dt_desdobr_aprov  = sysdate ' ||
			' where nr_seq_aprovacao= 		' || nr_seq_aprovacao_p ||
			' and nr_item_solic_compra in (	' || ds_seq_itens_p 	|| ')', ds_retorno_w);

ds_retorno_w := obter_valor_dinamico(	' update nota_fiscal_item 		' ||
			' set nr_seq_aprovacao	= 	' || nr_sequencia_w	||
			'    ,dt_desdobr_aprov  = sysdate ' ||
			' where nr_seq_aprovacao= 		' || nr_seq_aprovacao_p ||
			' and nr_item_nf in (	' || ds_seq_itens_p 	|| ')', ds_retorno_w);

ds_retorno_w := obter_valor_dinamico(	' update item_requisicao_material 	' ||
			' set nr_seq_aprovacao	= 	' || nr_sequencia_w	||
			'    ,dt_desdobr_aprov  = sysdate ' ||
			' where nr_seq_aprovacao= 		' || nr_seq_aprovacao_p ||
			' and nr_sequencia in (		' || ds_seq_itens_p 	|| ')', ds_retorno_w);

ds_retorno_w := obter_valor_dinamico(	' update cot_compra_item 		' ||
			' set nr_seq_aprovacao	= 	' || nr_sequencia_w	||
			'    ,dt_desdobr_aprov  = sysdate ' ||
			' where nr_seq_aprovacao= 		' || nr_seq_aprovacao_p ||
			' and nr_item_cot_compra in (		' || ds_seq_itens_p 	|| ')', ds_retorno_w);

ds_retorno_w := obter_valor_dinamico(	' update reg_lic_item 		' ||
			' set nr_seq_aprovacao	= 	' || nr_sequencia_w	||
			'    ,dt_desdobr_aprov  = sysdate ' ||
			' where nr_seq_aprovacao= 		' || nr_seq_aprovacao_p ||
			' and nr_seq_lic_item in (		' || ds_seq_itens_p 	|| ')', ds_retorno_w);

ds_retorno_w := obter_valor_dinamico(	' update contrato		' ||
			' set nr_seq_aprovacao	= 	' || nr_sequencia_w	||
			' ,dt_desdobr_aprov  = sysdate ' ||
			' where nr_seq_aprovacao= 		' || nr_seq_aprovacao_p ||
			' and nr_sequencia in (		' || ds_seq_itens_p 	|| ')', ds_retorno_w);

ds_retorno_w := obter_valor_dinamico(	' update projeto_recurso		' ||
			' set nr_seq_aprovacao	= 	' || nr_sequencia_w	||
			' ,dt_desdobr_aprov  = sysdate ' ||
			' where nr_seq_aprovacao= 		' || nr_seq_aprovacao_p ||
			' and nr_sequencia in (		' || ds_seq_itens_p 	|| ')', ds_retorno_w);

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
