CREATE OR REPLACE PROCEDURE desdobrar_solic_contrato ( nr_solic_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
					 
nr_solic_compra_w			solic_compra.nr_solic_compra%type;
nr_item_solic_compra_w		solic_compra_item.nr_item_solic_compra%type;
nr_item_solic_compra_ant_w		solic_compra_item.nr_item_solic_compra%type;
nr_item_solic_compra_entr_w		solic_compra_item_entrega.nr_item_solic_compra_entr%type;
qt_itens_com_contrato_w		bigint;
qt_itens_sem_contrato_w		bigint;	
 
c01 CURSOR FOR 
SELECT	nr_item_solic_compra 
from	solic_compra_item 
where	nr_solic_compra = nr_solic_compra_p 
and	obter_se_item_contrato(cd_material) = 'S';


BEGIN 
 
select	count(*) 
into STRICT	qt_itens_com_contrato_w 
from	solic_compra_item 
where	nr_solic_compra = nr_solic_compra_p 
and	obter_se_item_contrato(cd_material) = 'S';
 
select	count(*) 
into STRICT	qt_itens_sem_contrato_w 
from	solic_compra_item 
where	nr_solic_compra = nr_solic_compra_p 
and	obter_se_item_contrato(cd_material) = 'N';
	 
 
if (qt_itens_com_contrato_w > 0) and (qt_itens_sem_contrato_w > 0) then 
	 
	select	nextval('solic_compra_seq') 
	into STRICT	nr_solic_compra_w 
	;
	 
	insert into solic_compra( 
		nr_solic_compra, 
		cd_estabelecimento, 
		dt_solicitacao_compra, 
		dt_atualizacao, 
		nm_usuario, 
		ie_situacao, 
		cd_pessoa_solicitante, 
		cd_local_estoque, 
		cd_setor_atendimento, 
		cd_pessoa_autoriza, 
		ie_aviso_chegada, 
		ie_aviso_aprov_oc, 
		ie_urgente, 
		ie_tipo_solicitacao, 
		ie_comodato, 
		ie_semanal, 
		nm_usuario_nrec, 
		dt_atualizacao_nrec) 
	SELECT	nr_solic_compra_w, 
		cd_estabelecimento, 
		clock_timestamp(), 
		clock_timestamp(), 
		nm_usuario_p, 
		ie_situacao, 
		cd_pessoa_solicitante, 
		cd_local_estoque, 
		cd_setor_atendimento, 
		cd_pessoa_fisica, 
		ie_aviso_chegada, 
		ie_aviso_aprov_oc, 
		ie_urgente, 
		ie_tipo_solicitacao, 
		ie_comodato, 
		ie_semanal, 
		nm_usuario_p, 
		clock_timestamp() 
	from	solic_compra 
	where	nr_solic_compra = nr_solic_compra_p;
	 
	open C01;
	loop 
	fetch C01 into	 
		nr_item_solic_compra_ant_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		select	coalesce(max(nr_item_solic_compra),0) + 1 
		into STRICT	nr_item_solic_compra_w 
		from	solic_compra_item 
		where	nr_solic_compra = nr_solic_compra_w;
		 
		insert into solic_compra_item( 
			nr_solic_compra, 
			nr_item_solic_compra, 
			cd_material, 
			cd_unidade_medida_compra, 
			qt_material, 
			dt_atualizacao, 
			nm_usuario, 
			ie_situacao, 
			ds_observacao,		 
			dt_solic_item,		 
			vl_unit_previsto, 
			ie_geracao, 
			qt_conv_compra_est_orig, 
			qt_saldo_disp_estoque, 
			vl_custo_total, 
			vl_estoque_total, 
			qt_dias_estoque) 
		SELECT	nr_solic_compra_w, 
			nr_item_solic_compra_w, 
			cd_material, 
			cd_unidade_medida_compra, 
			qt_material, 
			clock_timestamp(), 
			nm_usuario_p, 
			ie_situacao, 
			ds_observacao,		 
			dt_solic_item, 
			vl_unit_previsto, 
			ie_geracao, 
			qt_conv_compra_est_orig, 
			qt_saldo_disp_estoque, 
			vl_custo_total, 
			vl_custo_total, 
			qt_dias_estoque 
		from	solic_compra_item 
		where	nr_solic_compra = nr_solic_compra_p 
		and	nr_item_solic_compra = nr_item_solic_compra_ant_w;
		 
		select	coalesce(max(nr_item_solic_compra_entr),0) +1 
		into STRICT	nr_item_solic_compra_entr_w 
		from	solic_compra_item_entrega 
		where	nr_solic_compra		= nr_solic_compra_w 
		and	nr_item_solic_compra	= nr_item_solic_compra_w;
		 
		insert into solic_compra_item_entrega( 
			nr_solic_compra, 
			nr_item_solic_compra, 
			nr_item_solic_compra_entr, 
			qt_entrega_solicitada, 
			dt_entrega_solicitada, 
			dt_atualizacao, 
			nm_usuario) 
		SELECT	nr_solic_compra_w, 
			nr_item_solic_compra_w, 
			nr_item_solic_compra_entr_w, 
			qt_entrega_solicitada, 
			dt_entrega_solicitada, 
			clock_timestamp(), 
			nm_usuario_p 
		from	solic_compra_item_entrega 
		where	nr_solic_compra = nr_solic_compra_p 
		and	nr_item_solic_compra = nr_item_solic_compra_ant_w;
		 
		delete from solic_compra_item 
		where	nr_solic_compra = nr_solic_compra_p 
		and	nr_item_solic_compra = nr_item_solic_compra_ant_w;
		 
		end;
	end loop;
	close C01;
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
