SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desdobrar_solic_grupo_compra ( nr_solic_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_item_solic_compra_w			integer;
nr_grupo_compra_w			bigint;
nr_grupo_compra_ww			bigint 	:= 0;
ie_primeira_vez_w				varchar(1)	:= 'S';
nr_solic_compra_nova_w			bigint;
cd_comprador_resp_w			varchar(10);
cd_material_w				integer;
cd_estabelecimento_w			smallint;
cd_local_estoque_w			bigint;
qt_registro_w				bigint;

c01 CURSOR FOR			
SELECT	nr_item_solic_compra,
	coalesce(obter_grupo_compras_material(cd_material),0) nr_grupo_compra,
	cd_material
from	solic_compra_item
where	nr_solic_compra = nr_solic_compra_p
order by nr_grupo_compra,
	nr_item_solic_compra;


BEGIN

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	solic_compra
where	nr_solic_compra = nr_solic_compra_p;

open C01;
loop
fetch C01 into	
	nr_item_solic_compra_w,
	nr_grupo_compra_w,
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	if (ie_primeira_vez_w = 'S') then
		nr_grupo_compra_ww := nr_grupo_compra_w;
	end if;
	
	if (nr_grupo_compra_ww <> nr_grupo_compra_w)  then
		begin
		
		select	nextval('solic_compra_seq')
		into STRICT	nr_solic_compra_nova_w
		;
		
		insert into solic_compra(
			nr_solic_compra,				cd_estabelecimento,				dt_solicitacao_compra,
			dt_atualizacao,				nm_usuario,					ie_situacao,
			cd_pessoa_solicitante,			cd_local_estoque,					cd_centro_custo,
			cd_conta_contabil,				cd_setor_atendimento,				ds_observacao,
			dt_liberacao,				ie_aviso_chegada,					nr_seq_ordem_serv,
			ie_aviso_aprov_oc,				ie_urgente,					nr_documento_externo,
			ie_forma_exportar,				nr_seq_planejamento,				nr_seq_proj_gpi,
			nr_seq_etapa_gpi,				nr_seq_conta_gpi,					cd_comprador_resp,
			ie_tipo_solicitacao,				nr_seq_ressup_fornec,				ie_comodato,
			ie_semanal,				ie_tipo_compra,					nr_seq_tipo_compra,
			nr_seq_mod_compra,			nr_seq_forma_compra,				nr_seq_motivo_solic,
			ds_justificativa,				cd_fornec_exclusivo,				cd_fornec_sugerido,
			nr_seq_motivo_urgente,			nr_prescricao,					nr_seq_prot_solic,
			nm_usuario_nrec,			dt_atualizacao_nrec,				nr_solic_compra_orig,
			ie_tipo_servico)
		SELECT	nr_solic_compra_nova_w,			cd_estabelecimento,				clock_timestamp(),
			clock_timestamp(),					nm_usuario_p,					ie_situacao,
			cd_pessoa_solicitante,			cd_local_estoque,					cd_centro_custo,
			cd_conta_contabil,				cd_setor_atendimento,				ds_observacao,
			dt_liberacao,				ie_aviso_chegada,					nr_seq_ordem_serv,
			ie_aviso_aprov_oc,				ie_urgente,					nr_documento_externo,
			ie_forma_exportar,				nr_seq_planejamento,				nr_seq_proj_gpi,
			nr_seq_etapa_gpi,				nr_seq_conta_gpi,					null,
			ie_tipo_solicitacao,				nr_seq_ressup_fornec,				ie_comodato,
			ie_semanal,				ie_tipo_compra,					nr_seq_tipo_compra,
			nr_seq_mod_compra,			nr_seq_forma_compra,				nr_seq_motivo_solic,
			ds_justificativa,				cd_fornec_exclusivo,				cd_fornec_sugerido,
			nr_seq_motivo_urgente,			nr_prescricao,					nr_seq_prot_solic,
			nm_usuario_p,				clock_timestamp(),					nr_solic_compra_p,
			ie_tipo_servico
		from	solic_compra
		where	nr_solic_compra = nr_solic_compra_p;
		
		select	cd_local_estoque
		into STRICT	cd_local_estoque_w
		from	solic_compra
		where	nr_solic_compra = nr_solic_compra_p;
		
		insert into solic_compra_item(
			nr_solic_compra,				nr_item_solic_compra,				cd_material,
			cd_unidade_medida_compra,			qt_material,					dt_atualizacao,
			nm_usuario,				ie_situacao,					ds_material_direto,
			ds_observacao,				nr_cot_compra,					nr_item_cot_compra,
			cd_motivo_baixa,				dt_baixa,						dt_solic_item,
			vl_unit_previsto,				ie_geracao,					nr_seq_proj_rec,
			ie_forma_exportar,				nr_solic_compra_ref,				nr_item_solic_compra_ref,
			cd_conta_contabil,				ds_observacao_int,					nr_contrato,
			ie_bula,					ie_amostra,					ie_catalogo,
			qt_conv_compra_est_orig,			qt_saldo_disp_estoque,  nr_seq_orc_item_gpi)
		SELECT	nr_solic_compra_nova_w,			nr_item_solic_compra,				cd_material,
			cd_unidade_medida_compra,			qt_material,					clock_timestamp(),
			nm_usuario_p,				ie_situacao,					ds_material_direto,
			ds_observacao,				nr_cot_compra,					nr_item_cot_compra,
			cd_motivo_baixa,				dt_baixa,						dt_solic_item,
			vl_unit_previsto,				ie_geracao,					nr_seq_proj_rec,
			ie_forma_exportar,				nr_solic_compra_p,					nr_item_solic_compra_w,
			cd_conta_contabil,				ds_observacao_int,					nr_contrato,
			ie_bula,					ie_amostra,					ie_catalogo,
			obter_dados_material(cd_material,'QCE'),	obter_saldo_disp_estoque(cd_estabelecimento_w, cd_material, cd_local_estoque_w, trunc(clock_timestamp(),'mm')),
            nr_seq_orc_item_gpi
		from	solic_compra_item
		where	nr_solic_compra = nr_solic_compra_p
		and	coalesce(obter_grupo_compras_material(cd_material),0) = nr_grupo_compra_w;
		
		insert into solic_compra_item_entrega(
			nr_solic_compra,
			nr_item_solic_compra,
			nr_item_solic_compra_entr,
			qt_entrega_solicitada,
			dt_entrega_solicitada,
			dt_atualizacao,
			nm_usuario,
			ds_observacao)
		SELECT	nr_solic_compra_nova_w,
			a.nr_item_solic_compra,
			a.nr_item_solic_compra_entr,
			a.qt_entrega_solicitada,
			a.dt_entrega_solicitada,
			clock_timestamp(),
			nm_usuario_p,
			a.ds_observacao
		from	solic_compra_item_entrega a,
			solic_compra_item b
		where	a.nr_solic_compra = b.nr_solic_compra
		and	a.nr_item_solic_compra = b.nr_item_solic_compra
		and	a.nr_solic_compra = nr_solic_compra_p
		and	coalesce(obter_grupo_compras_material(b.cd_material),0) = nr_grupo_compra_w;		
		
		if (nr_grupo_compra_w > 0) then
			select	cd_comprador
			into STRICT	cd_comprador_resp_w
			from	sup_grupo_compra
			where	nr_sequencia = nr_grupo_compra_w;
			
		end if;
		
		if (cd_comprador_resp_w IS NOT NULL AND cd_comprador_resp_w::text <> '') then
		
			select	count(*)
			into STRICT	qt_registro_w
			from	comprador
			where	cd_pessoa_fisica = cd_comprador_resp_w
			and	cd_estabelecimento = cd_estabelecimento_w;
			
			if (qt_registro_w > 0) then		
				update	solic_compra
				set	cd_comprador_resp = coalesce(cd_comprador_resp_w, obter_comprador_material(cd_material_w, ie_urgente, cd_estabelecimento_w))
				where	nr_solic_compra = nr_solic_compra_nova_w;
			end if;
		end if;
		
		delete from solic_compra_item
		where	nr_solic_compra = nr_solic_compra_p
		and	coalesce(obter_grupo_compras_material(cd_material),0) = nr_grupo_compra_w;
		
		end;
	end if;
	nr_grupo_compra_ww := nr_grupo_compra_w;	
	
	ie_primeira_vez_w := 'N';	

	end;
end loop;
close C01;

select	coalesce(max(obter_grupo_compras_material(cd_material)),0) nr_grupo_compra
into STRICT	nr_grupo_compra_w
from	solic_compra_item
where	nr_solic_compra = nr_solic_compra_p;

if (nr_grupo_compra_w > 0) then
	select	cd_comprador
	into STRICT	cd_comprador_resp_w
	from	sup_grupo_compra
	where	nr_sequencia = nr_grupo_compra_w;
end if;

select	coalesce(min(nr_item_solic_compra),0)
into STRICT	nr_item_solic_compra_w
from	solic_compra_item
where	nr_solic_compra = nr_solic_compra_p;

if (nr_item_solic_compra_w > 0) then
	select	cd_material
	into STRICT	cd_material_w
	from	solic_compra_item
	where	nr_solic_compra = nr_solic_compra_p
	and	nr_item_solic_compra = nr_item_solic_compra_w;
	
	if (cd_comprador_resp_w IS NOT NULL AND cd_comprador_resp_w::text <> '') then
		
		select	count(*)
		into STRICT	qt_registro_w
		from	comprador
		where	cd_pessoa_fisica = cd_comprador_resp_w
		and	cd_estabelecimento = cd_estabelecimento_w;
		
		if (qt_registro_w > 0) then	
			update	solic_compra
			set	cd_comprador_resp = coalesce(cd_comprador_resp_w, obter_comprador_material(cd_material_w, ie_urgente, cd_estabelecimento_w))
			where	nr_solic_compra = nr_solic_compra_p;
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE desdobrar_solic_grupo_compra ( nr_solic_compra_p bigint, nm_usuario_p text) FROM PUBLIC;

