SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desfazer_alta_tesouraria ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_alta_tesouraria_w timestamp;			
			 

BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	select	dt_alta_tesouraria 
	into STRICT 	dt_alta_tesouraria_w 
	from	atendimento_paciente 
	where 	nr_atendimento = nr_atendimento_p;
 
	if (dt_alta_tesouraria_w IS NOT NULL AND dt_alta_tesouraria_w::text <> '') then 
		update	atendimento_paciente 
		set	dt_alta_tesouraria 	 = NULL, 
			nm_usuario	= nm_usuario_p 
		where	nr_atendimento	= nr_atendimento_p;
		 
		CALL Gravar_Hist_Alta_Saida_Real(nr_atendimento_p, wheb_mensagem_pck.get_texto(791742), clock_timestamp(), nm_usuario_p);
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
