CREATE OR REPLACE PROCEDURE desfazer_aprovacao_orcamento (nr_sequencia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


qt_reg_venc_w			integer;
cd_condicao_pagamento_w		bigint;
ie_limpar_desconto_w		varchar(1);


BEGIN

ie_limpar_desconto_w := obter_valor_param_usuario(106, 120, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p);

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	update 	orcamento_paciente
	set 	ie_status_orcamento  = 1,
		dt_aprovacao 	      = NULL,
		nm_usuario_aprovacao = nm_usuario_p
	where 	nr_sequencia_orcamento = nr_sequencia_p;

end if;

if (coalesce(ie_limpar_desconto_w,'N') = 'S') then
	update 	orcamento_paciente
	set	vl_desconto  = NULL
	where	nr_sequencia_orcamento = nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
