CREATE OR REPLACE PROCEDURE desfazer_assinatura (NR_PRESCRICAO_P bigint, NR_SEQ_PRESCR_P bigint) AS $body$
DECLARE


nr_seq_resultado_w		bigint;

BEGIN
  select MAX(nr_seq_resultado)
    into STRICT nr_seq_resultado_w
    from exame_lab_resultado
   where nr_prescricao = nr_prescricao_p;

  if (nr_seq_resultado_w IS NOT NULL AND nr_seq_resultado_w::text <> '') then
    update exame_lab_result_item
       set nr_Seq_Assinatura  = NULL
	 	 where nr_sequencia      = NR_SEQ_PRESCR_P
       and nr_seq_resultado  = nr_seq_resultado_w
       and (nr_seq_material IS NOT NULL AND nr_seq_material::text <> '');
	end if;

  commit;
END;
