CREATE OR REPLACE PROCEDURE desfazer_baixa_prazo_sup ( nr_prescricao_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
BEGIN

update	prescr_procedimento a
set	a.cd_motivo_baixa	= 0,
	a.dt_baixa		 = NULL,
	a.nm_usuario		= nm_usuario_p,
	a.ie_status_execucao	= '10'
where	a.nr_prescricao		= nr_prescricao_p
and	a.nr_sequencia		= nr_sequencia_p
and	a.cd_motivo_baixa	<> 0
and	not exists (	SELECT	1
			from	procedimento_paciente b
			where	b.nr_prescricao			= a.nr_prescricao
			and	b.nr_sequencia_prescricao	= a.nr_sequencia);

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
