SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desfazer_bordero_darf_gps ( nr_seq_darf_p bigint, nr_seq_gps_p bigint, nr_bordero_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint ) AS $body$
DECLARE


nr_titulo_w						darf_titulo_pagar.nr_titulo%type;
dt_real_pagamento_w				timestamp;
ie_selec_tit_outro_bordero_w	varchar(1);

c01 CURSOR FOR
SELECT	nr_titulo
from	darf_titulo_pagar
where	nr_seq_darf = nr_seq_darf_p

union	all

PERFORM	nr_titulo
from	gps_titulo
where	nr_seq_gps	= nr_seq_gps_p;


BEGIN

select 	dt_real_pagamento,
		coalesce(IE_TITULO_BORDERO,'S')
into STRICT	dt_real_pagamento_w,
		ie_selec_tit_outro_bordero_w
from	bordero_pagamento
where	nr_bordero= nr_bordero_p;
	
if (dt_real_pagamento_w IS NOT NULL AND dt_real_pagamento_w::text <> '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266522);
end if;

-- Verificar o parametro do usuario se for N apenas realizar UPDATE na tabela de titulos a pagar se for S realizar DELETE na tabela BORDERO_TIT_PAGAR
if (ie_selec_tit_outro_bordero_w = 'N') then

	open c01;
	loop
	fetch c01 into	
		nr_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		
		update	titulo_pagar
		set	nr_bordero  = NULL,
			vl_bordero  = NULL
		where	nr_titulo = nr_titulo_w;
		commit;
		
		end;
	end loop;
	close c01;

elsif (ie_selec_tit_outro_bordero_w = 'S') then

	delete from bordero_tit_pagar where nr_bordero = nr_bordero_p;

end if;

-- Excluir o numero do bordero da tabela da DARF
if (nr_seq_darf_p IS NOT NULL AND nr_seq_darf_p::text <> '') then
	update	darf
	set	nr_bordero	 = NULL
	where	nr_sequencia	= nr_seq_darf_p;
else
	update	gps
	set	nr_bordero	 = NULL
	where	nr_sequencia	= nr_seq_gps_p;
end if;

update bordero_pagamento
set dt_cancelamento = clock_timestamp(), 
	dt_atualizacao = clock_timestamp(), 
	nm_usuario = nm_usuario_p
where nr_bordero = nr_bordero_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
