SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desfazer_bordero_darf ( nr_seq_darf_p bigint, nr_bordero_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint ) AS $body$
DECLARE


nr_titulo_w			darf_titulo_pagar.nr_titulo%type;
qt_registros_w			bigint;
ie_selec_tit_outro_bordero_w		varchar(1);

c01 CURSOR FOR
SELECT	nr_titulo
from	darf_titulo_pagar
where	nr_seq_darf = nr_seq_darf_p;


BEGIN

select 	coalesce(IE_TITULO_BORDERO,'S')
into STRICT	ie_selec_tit_outro_bordero_w
from	bordero_pagamento
where	nr_bordero= nr_bordero_p;

-- Verifica se ja foi baixado
select 	count(*)
into STRICT	qt_registros_w
from	titulo_pagar_baixa
where	nr_bordero= nr_bordero_p;
	
if (qt_registros_w > 0) then
	/*'Nao e possivel excluir o bordero, pois os titulos ja baixados vinculados a este bordero!;*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(266522);
end if;

-- Verificar o parametro do usuario, se for N apenas realizar UPDATE na tabela de titulos a pagar; se for S realizar DELETE na tabela BORDERO_TIT_PAGAR.
if (ie_selec_tit_outro_bordero_w = 'N') then

	open c01;
	loop
	fetch c01 into	
		nr_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		
		update	titulo_pagar
		set	nr_bordero  = NULL,
			vl_bordero  = NULL
		where	nr_titulo = nr_titulo_w;
		commit;
		
		end;
	end loop;
	close c01;

elsif (ie_selec_tit_outro_bordero_w = 'S') then

	delete from bordero_tit_pagar where nr_bordero = nr_bordero_p;

end if;

-- Excluir o numero do bordero da tabela da DARF
update	darf
set	nr_bordero	 = NULL
where	nr_sequencia	= nr_seq_darf_p;

-- Excluir da tabela do bordero
delete from bordero_pagamento
where nr_bordero = nr_bordero_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
