CREATE OR REPLACE PROCEDURE desfazer_conferencia_envelope ( nr_seq_envelope_p bigint, nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
SELECT	nr_seq_laudo,
	nr_seq_result_lab
from	envelope_laudo_item
where	nr_seq_envelope = nr_seq_envelope_p;

nr_Seq_laudo_w			bigint;
nr_seq_result_lab_w		bigint;


BEGIN

update	envelope_laudo
set	dt_checagem  = NULL,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp(),
	nr_seq_status_envelope = CASE WHEN nr_seq_status_envelope=15 THEN  10  ELSE nr_seq_status_envelope END
where	nr_sequencia = nr_seq_envelope_p;

open C01;
loop
fetch C01 into
	nr_seq_laudo_w,
	nr_seq_result_lab_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL gel_gravar_historico_envelope(nr_seq_laudo_w, nr_seq_result_lab_w, nr_seq_envelope_p, null, 'DEC', nm_usuario_p);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
