CREATE OR REPLACE PROCEDURE desfazer_conf_ordem (nr_sequencia_p bigint, nm_usuario_p text, ds_mensagem_p INOUT text) AS $body$
DECLARE


ie_existe_w	varchar(1);
ds_mensagem_w	varchar(255);


BEGIN
select 	coalesce(max('S'),'N')
into STRICT	ie_existe_w
from 	can_ordem_prod
where 	(nr_seq_etapa_prod IS NOT NULL AND nr_seq_etapa_prod::text <> '')
and 	nr_sequencia = nr_sequencia_p;

if (ie_existe_w = 'S') then
	ds_mensagem_w := obter_texto_tasy(177758, wheb_usuario_pck.get_nr_seq_idioma);
	ds_mensagem_p := ds_mensagem_w;
end if;

if (coalesce(ds_mensagem_w::text, '') = '') then
	update 	can_ordem_prod
	set 	nm_usuario_conf = nm_usuario_p,
		ie_conferido = 'N'
	where	nr_sequencia = nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
