SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE desfazer_deposito (nr_seq_deposito_p bigint, dt_atualizacao_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_seq_cheque_w		bigint;
dt_deposito_w			timestamp;
dt_reapresentacao_w		timestamp;
dt_seg_reapresentacao_w	timestamp;

c01 CURSOR FOR 
SELECT	c.nr_seq_cheque, 
	c.dt_deposito, 
	c.dt_reapresentacao, 
	c.dt_seg_reapresentacao 
from	cheque_cr c, 
	deposito_cheque b 
where	b.nr_seq_deposito	= nr_seq_deposito_p 
and	b.nr_seq_cheque	= c.nr_seq_cheque;


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_seq_cheque_w, 
	dt_deposito_w, 
	dt_reapresentacao_w, 
	dt_seg_reapresentacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	/* Desfazendo segunda reapresentação */
 
	if (dt_seg_reapresentacao_w IS NOT NULL AND dt_seg_reapresentacao_w::text <> '') then 
		update	cheque_cr 
		set	dt_atualizacao		= clock_timestamp(), 
			nm_usuario		= nm_usuario_p, 
			dt_seg_reapresentacao	 = NULL 
		where	nr_seq_cheque		= nr_seq_cheque_w;
 
		CALL gerar_cheque_cr_hist(nr_seq_cheque_w,wheb_mensagem_pck.get_texto(304948),'N',nm_usuario_p);
											  
	end if;
 
	/* Desfazendo reapresentação */
 
	if (dt_reapresentacao_w IS NOT NULL AND dt_reapresentacao_w::text <> '') and (coalesce(dt_seg_reapresentacao_w::text, '') = '') then 
		update	cheque_cr 
		set	dt_atualizacao		= clock_timestamp(), 
			nm_usuario		= nm_usuario_p, 
			dt_reapresentacao	 = NULL 
		where	nr_seq_cheque		= nr_seq_cheque_w;
 
		CALL gerar_cheque_cr_hist(nr_seq_cheque_w,wheb_mensagem_pck.get_texto(304945),'N',nm_usuario_p);
	end if;
 
	/* Desfazendo depósito */
 
	if (dt_deposito_w IS NOT NULL AND dt_deposito_w::text <> '') and (coalesce(dt_reapresentacao_w::text, '') = '') then 
		update	cheque_cr 
		set	dt_atualizacao		= clock_timestamp(), 
			nm_usuario		= nm_usuario_p, 
			dt_deposito		 = NULL 
		where	nr_seq_cheque		= nr_seq_cheque_w;
 
		CALL gerar_cheque_cr_hist(nr_seq_cheque_w,wheb_mensagem_pck.get_texto(304946),'N',nm_usuario_p);
	end if;
 
	CALL ATUALIZAR_DESBLOQUEIO_CHEQUE(nr_seq_cheque_w, nm_usuario_p);
	CALL atualizar_saldo_neg_cheque_cr(nr_seq_cheque_w,nm_usuario_p);
	CALL atualizar_cobranca_cheque(nr_seq_cheque_w,nm_usuario_p);
 
end loop;
close c01;
 
update	deposito 
set	dt_atualizacao	= clock_timestamp(), 
	nm_usuario	= nm_usuario_p, 
	dt_deposito	 = NULL 
where	nr_sequencia	= nr_seq_deposito_p;
 
CALL GERAR_MOVTO_DEPOSITO(nr_seq_deposito_p,dt_atualizacao_p,nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
